    const defaultContacto = new Contacto(
        'Carlos', 'Perez', 'carlosperez@mail.com', true
    );

    const [conectado, changeState] = useState(defaultContacto.estaConectado);

    const cambiarEstado = () => {
        console.log("to do");
        changeState(!conectado);

        console.log(conectado);
    }

    return (
        <div>
            <h2>
                Your contact:
            </h2>
            <ComponenteB contacto={ defaultContacto } ></ComponenteB>
            <button onClick={ cambiarEstado }>Cambiar estado</button>
        </div>
    );
};


export default ComponenteA;
