import React, { useState } from 'react';
import { Contacto } from '../../models/contacto.class'
import ComponenteB from '../pure/componenteB';


const ComponenteA = () => {

    const defaultContacto = new Contacto(
        'Carlos', 'Perez', 'carlosperez@mail.com', true
    );



    const changeState = () => {
        console.log("to do");
        console.log(defaultContacto.estaConectado);
    }

    return (
        <div>
            <h2>
                Your contact:
            </h2>
            <ComponenteB contacto={ defaultContacto } ></ComponenteB>
            <button onClick={ changeState }>Cambiar estado</button>
        </div>
    );
};


export default ComponenteA;
